INSERT INTO `articles` (`id`, `title`, `body`, `created`, `modified`) VALUES
	(4, 'Заработал новый сайт компании ', '<p>Сайт компании &quot;Владком&quot; подвергся тотальному обновлению. Сменился дизайн сайта, навигация, и самое главное &mdash; наполнение.</p>\n\n<p>Добавлены новые <a href="http://vladcom.su/catalog/oborudovanie-dlya-obrabotki-listovogo-metalla/" title="Оборудование для обработки листового металла">станки для обработки листового метала</a>, обновлен список цен на <a href="http://vladcom.su/catalog/kameri-polimerizacii/" title="Камеры полимеризации">печи полимеризации</a> и <a href="http://vladcom.su/catalog/okrasochnye-kameri/" title="Окрасочные камеры">камеры напыления</a>, представлены <a href="http://vladcom.su/catalog/okrasochnye-kompleksi/" title="Готовые окрасочные комплексы">коммерческие предложения</a> готовых окрасочных комплексов.</p>\n', '2013-04-05 11:20:52', '2013-08-29 09:12:56'),
	(5, 'Оборудование для порошковой окраски', '<p>На нашем сайте представлен широкий ассортимент оборудования для порошковой окраски. В соответствующем разделе вы можете найти цены на печи полимеризации, камеры напыления, а также заказать готовый окрасочный комплекс с транспортной системой на ваш выбор.</p>\n\n<p>Покрасочное оборудование от компании &quot;Владком&quot; - это гарантия качества и надежности.</p>\n', '2013-08-29 09:12:01', '0000-00-00 00:00:00');
INSERT INTO `blocks` (`id`, `slug`, `title`, `body`, `created`, `modified`) VALUES
	(4, 'header', 'Шапка', '<div class="container header">\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td class="logo"><img class="logo img-responsive" src="/assets/pics/new_logo.png" alt="" /></td>\r\n<td class="description">\r\n<h3>Производственно-торговая компания &laquo;Владком&raquo;</h3>\r\n<p>Компания &laquo;ВладКом&raquo;&nbsp;&mdash; это проектно-производственное предприятие, которое имеет в&nbsp;своем составе конструкторское бюро, производственные площади с&nbsp;современным оборудованием и&nbsp;офис продаж в&nbsp;г. Чебоксары.</p>\r\n<p>Мы&nbsp;предлагаем вам оборудование для порошковой окраски (камеры полимеризации, печи напыления и&nbsp;окрасочные комплексы), оборудование для обработки листового металла (листогибы гидравлические, ножницы гильотинные, станки лазерной резки).&nbsp;Наши специалисты помогут вам подготовить техническое задание, разработать и&nbsp;согласовать проект, проведут монтаж оборудования, его пуско-наладку и&nbsp;должным образом обучат ваш персонал.</p>\r\n</td>\r\n<td class="contacts">\r\n<div class="pull-right nav-contacts">\r\n<p class="contact-p">+7 (917) 668 37 40</p>\r\n<p class="contact-p">+7 (917) 078 96 12</p>\r\n<p class="contact-p">+7 (8352) 22 96 12</p>\r\n<p class="contact-email"><a title="Написать письмо" href="mailto:vladcom.su@mail.ru">vladcom.su@mail.ru</a></p>\r\n</div>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>', '0000-00-00 00:00:00', '2013-10-24 10:55:22'),
	(5, 'footer', 'Подвал', '<div class="container footer">\r\n<div class="row">\r\n<div class="col-xs-6 copyright">&copy; dpv</div>\r\n<div class="col-xs-6 contacts">\r\n<div class="pull-right nav-contacts">\r\n<p class="contact-p">+7 (917) 668 37 40</p>\r\n<p class="contact-p">+7 (917) 078 96 12</p>\r\n<p class="contact-p">+7 (8352) 22 96 12</p>\r\n<p class="contact-email"><a title="Написать письмо" href="mailto:vladcom.su@mail.ru"> vladcom.su@mail.ru </a></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>', '0000-00-00 00:00:00', '2013-09-09 15:53:36'),
	(6, 'hero-unit', 'Спецпредложение', '<div class="main-hero-unit">\r\n<div class="row">\r\n<div class="col-xs-5"><a title="Комплекс с комбинированной транспортной системой всего за 420 000 руб." href="/catalog/3.0x1.6x1.8-kompleks-s-kombinirovannojj-transportnojj-sistemoj/"> <img class="img-responsive" src="/assets/pics/kompleks-s-kombinirovannojj-transportnojj-sistemojj.jpg" alt="" /> </a></div>\r\n<div class="col-xs-7">\r\n<p class="slogan">Спецпредложение!</p>\r\n<p class="title"><a title="Комплекс с комбинированной транспортной системой всего за 420 000 руб." href="/catalog/3.0x1.6x1.8-kompleks-s-kombinirovannojj-transportnojj-sistemoj/">Окрасочный комплекс с комбинированной транспортной системой под ключ</a></p>\r\n<p class="size">Размер печи полимеризации, м (Д/Ш/В): 3 м / 1.6 м / 1.8 м</p>\r\n<p><span style="text-decoration: line-through; font-size: 18px;">460 000 руб.</span>&nbsp; &nbsp;&nbsp;<span style="color: #c72929; font-size: 35px;">420 000 руб.</span></p>\r\n<p class="note">* Количество комплексов ограничено. Поторопитесь!</p>\r\n</div>\r\n</div>\r\n</div>', '0000-00-00 00:00:00', '2013-09-12 14:36:25'),
	(7, 'product-samples', 'Примеры продукции', '<div class="label-of-block">Примеры продукции</div>\r\n<div class="row">\r\n<div class="col-xs-3">\r\n<div><img class="img-responsive" src="/assets/pics/oborudovanie-dlya-poroshkovojj-okraski-2.jpg" alt="" /> <small>Камера напыления</small></div>\r\n</div>\r\n<div class="col-xs-3">\r\n<div><img class="img-responsive" src="/assets/pics/oborudovanie-dlya-poroshkovojj-okraski-3.jpg" alt="" /> <small>Печь полимеризации</small></div>\r\n</div>\r\n<div class="col-xs-3">\r\n<div><img class="img-responsive" src="/assets/pics/oborudovanie-dlya-obrabotki-listovogo-metalla-2.jpg" alt="" /> <small>Листогиб гидравлический</small></div>\r\n</div>\r\n<div class="col-xs-3">\r\n<div><img class="img-responsive" src="/assets/pics/oborudovanie-dlya-obrabotki-listovogo-metalla-3.jpg" alt="" /> <small>Листогиб гидравлический</small></div>\r\n</div>\r\n</div>', '2013-09-09 15:14:59', '2013-09-09 15:14:59');
